mapper.select:
select

from entity_statistic
where entity_type = #{entity} and entity_id = #{entityId}

mapper.update:
update entity_statistic
set
visit_count = #{visitCount}
where entity_type = #{entity} and entity_id = #{entityId}

update entity_statistic
set
like_count = #{likeCount}
where entity_type = #{entity} and entity_id = #{entityId}

update entity_statistic
set
collect_count = #{collectCount}
where entity_type = #{entity} and entity_id = #{entityId}

mapper.delete:
delete
from entity_statistic
where entity_type = #{entity} and entity_id = #{entityId}
